mapper.update:
UPDATE RecommendBoard
SET title = #{title}, content=#{content}
WHERE boardno = #{boardNo}

UPDATE RecommendBoard
SET viewcnt = viewcnt+1
WHERE boardno = #{boardNo}

mapper.select:

SELECT * FROM 
	(SELECT @ROWNUM:=@ROWNUM+1 as rn, boardno, title, writer, content, regdate, viewcnt 
		FROM RecommendBoard, (SELECT @ROWNUM:=0) TMP ORDER BY boardno DESC
	) RecommendBoard
WHERE rn > (#{page}-1) * #{countPerPage} AND rn <= #{page} * #{countPerPage}
